import React, {useState} from "react";
import "./Home.css";
import logo from "./img/logo-2.png";

function Home() {
  // fixed Header
  window.addEventListener("click", function () {
    const header = document.querySelector(".header");
    header.classList.toggle("active", window.scrollY > 0);
  });
  // Toogle Menu
   const [show, setShow] = useState(false);

   const [disableBtn, setDisableBtn] = useState(false);
  
   const Clickout = (eve) => {
    if (showMenu && ref.current && !ref.current.contains(eve.target)) {
      setShow(false);
      setDisableBtn(true)
    } else {
      setDisableBtn(false)
    }
  };

  return (
    <div className="home" id="Home">
      <div className="home__bg">
        <div className="header d__flex align__items__center pxy__30">
          <div className="logo">
            <img src={logo} alt="" />
          </div>
          <div className="navigation pxy__30">
            <ul className="navbar d__flex">
              <a href="#Home">
                <li className="nav__items mx__15">Home</li>
              </a>
              <a href="#About">
                <li className="nav__items mx__15">About</li>
              </a>
              <a href="#Portfolio">
                <li className="nav__items mx__15">Projects</li>
              </a>
              <a href="#Contact">
                <li className="nav__items mx__15">Contact</li>
              </a>
              <a href="mailto:ruttymat@gmail.com">
                <li className="nav__items mx__15">EmailMe</li>
              </a>
            </ul>
          </div>
          {/* Toogle Menu */}
          <div className="toggle__menu">
            <svg onClick={() => setShow(!show)}
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              class="bi bi-justify white pointer"
              viewBox="0 0 16 16"
            >
              <path
                fill-rule="evenodd"
                d="M2 12.5a.5.5 0 0 1 .5-.5h11a.5.5 0 0 1 0 1h-11a.5.5 0 0 1-.5-.5zm0-3a.5.5 0 0 1 .5-.5h11a.5.5 0 0 1 0 1h-11a.5.5 0 0 1-.5-.5zm0-3a.5.5 0 0 1 .5-.5h11a.5.5 0 0 1 0 1h-11a.5.5 0 0 1-.5-.5zm0-3a.5.5 0 0 1 .5-.5h11a.5.5 0 0 1 0 1h-11a.5.5 0 0 1-.5-.5z"
              />
            </svg>
          </div>
          {show ?(
          <div className="sideNavbar">
              <ul className="sidebar d__flex" id="menu">
              <li >
              <a className="sidebar__item" href="#Home">Home</a>
            </li>
            <li >
              <a  className="sidebar__item" href="#About">About</a>
            </li>
            <li >
              <a className="sidebar__item" href="mailto:ruttymat98@gmail.com">EmailMe</a>
            </li>
            <li >
              <a  className="sidebar__item" href="#Portfolio">Projects</a>
            </li>
            <li >
              <a className="sidebar__item" href="#Contact">Contact</a>
            </li>
              </ul>
          </div>
           ) : null}
        </div>
        {/* HOME CONTENT */}
        <div className="container">
          <div className="home__content">
            <div className="home__meta">
              <h2 className="home__text pz__10">Hi, I’m Ruth Mathias</h2>
              <h3 className="home__text sweet pz__10">Frontend Developer.</h3>
              <h4 className="home__text pz__10">based in Nigeria.</h4>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
